import React, { useContext, useState } from "react";
import { Container, Row, Col } from "react-bootstrap";
import { withRouter } from "react-router-dom";
import { withCookies } from "react-cookie";
import { Context } from "../../Context";

function Logout({ cookies, history }) {
  const [form, setForm] = useState({});
  const { setLoginStatus } = useContext(Context);

  function handleLogout(e) {
    e.preventDefault();
    // fetch("http://localhost:4000/user/sign_in", {
    //   method: "POST",
    //   headers: {
    //     "Content-Type": "application/json",
    //   },
    //   body: JSON.stringify(form),
    // })
    //   .then((data) => data.json())
    //   .then((token) => {
    cookies.remove("token", {
      path: "/",
      maxAge: 30 * 60,
    });
    setLoginStatus(false);
    setTimeout(() => {
      history.push("/home");
    }, 2000);

    // });
  }

  return (
    <Container>
      <Row className="justify-content-center">
        <Col lg="6" md="8" xs="10">
          'You have been succesfully log out and you'll be redirected to home
          screen
        </Col>
      </Row>
    </Container>
  );
}

export default withRouter(withCookies(Logout));
